  customer_phone,
  event_date,
  ready_by_time,
  event_start_time,
  service_location,
  current_status,
  payment_status,
  created_at,
  updated_at
) VALUES (
  gen_random_uuid(),
  'le-glam-team',
  'CUST-' || FLOOR(EXTRACT(EPOCH FROM NOW()) * 1000)::bigint
           || '.' || LPAD(FLOOR(RANDOM()*[redacted-phone])::text, 6, '0'),

  NULLIF({{ AddCustomerForm.data.customer_name }}, ''),
  NULLIF({{ AddCustomerForm.data.customer_email }}, ''),
  NULLIF({{ AddCustomerForm.data.customer_phone }}, ''),

  /* event_date: sadece tarih yaz */
  CASE
    WHEN NULLIF({{ AddCustomerForm.data.event_date }}, '') IS NULL
      THEN NULL
    WHEN POSITION('T' IN {{ AddCustomerForm.data.event_date }}) > 0
      THEN LEFT({{ AddCustomerForm.data.event_date }}, 10)::date
    ELSE NULLIF({{ AddCustomerForm.data.event_date }}, '')::date
  END,

  /* ready_by_time: HH:MM[:SS][.mmm] veya timestamp gelebilir; boşsa 00:00:00 */
  CASE
    WHEN NULLIF({{ AddCustomerForm.data.ready_by_time }}, '') IS NULL
      THEN '00:00:00'::time
    WHEN NULLIF({{ AddCustomerForm.data.ready_by_time }}, '') ~ '^\d{2}:\d{2}(:\d{2})?(\.\d+)?$'
      THEN regexp_replace(NULLIF({{ AddCustomerForm.data.ready_by_time }}, ''), '\.\d+$', '')::time
    ELSE (NULLIF({{ AddCustomerForm.data.ready_by_time }}, '')::timestamptz)::time
  END,

  /* event_start_time: HH:MM[:SS][.mmm] veya timestamp; milisaniyeyi at */
  CASE
    WHEN NULLIF({{ AddCustomerForm.data.event_start_time }}, '') IS NULL
      THEN NULL
    WHEN NULLIF({{ AddCustomerForm.data.event_start_time }}, '') ~ '^\d{2}:\d{2}(:\d{2})?(\.\d+)?$'
      THEN regexp_replace(NULLIF({{ AddCustomerForm.data.event_start_time }}, ''), '\.\d+$', '')::time
    ELSE (NULLIF({{ AddCustomerForm.data.event_start_time }}, '')::timestamptz)::time
  END,

  NULLIF({{ AddCustomerForm.data.service_location }}, ''),

  'New Submission',
  'unpaid',
  NOW(),
  NOW()
